// App.js
import React, { useState, useEffect } from "react";
import "./index.css";
import { Button } from "./components/ui/button";
import { Select } from "./components/ui/select.jsx";

function App() {
  const [homeTeam, setHomeTeam] = useState("");
  const [awayTeam, setAwayTeam] = useState("");
  const [teamOptions, setTeamOptions] = useState([]);
  const [result, setResult] = useState(null);
  const [probs, setProbs] = useState(null);
  const [rankings, setRankings] = useState({ home: null, away: null });

  useEffect(() => {
    fetch("http://127.0.0.1:8000/teams")
      .then((res) => res.json())
      .then((data) => {
        const options = data.teams.map((teamName) => ({
          label: teamName.replace(" Football Club", ""), // 表示を簡潔に
          value: teamName, // 正式名称を送る
        }));
        setTeamOptions(options);
      })
      .catch((err) => console.error("Failed to fetch teams:", err));
  }, []);  

  const handlePredict = async () => {
    try {
      const featureRes = await fetch(
        `http://127.0.0.1:8000/features?home=${homeTeam}&away=${awayTeam}`
      );
      const featureData = await featureRes.json();
      setRankings({ home: featureData.home_rank, away: featureData.away_rank });

      const res = await fetch("http://127.0.0.1:8000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ features: featureData.features }),
      });
      const data = await res.json();
      setResult(data.result);
      setProbs(data.probabilities);
    } catch (error) {
      console.error("Prediction failed:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6 text-center">
      <h1 className="text-3xl font-bold mb-6 text-blue-600">プレミアリーグ勝敗予測</h1>

      <Select
        label="ホームチーム"
        value={homeTeam}
        onChange={(e) => setHomeTeam(e.target.value)}
        options={teamOptions}
      />

      <Select
        label="アウェイチーム"
        value={awayTeam}
        onChange={(e) => setAwayTeam(e.target.value)}
        options={teamOptions}
      />

      <Button
        onClick={handlePredict}
        disabled={!homeTeam || !awayTeam}
        variant="outline" size="lg"
      >
        予測する
      </Button>

      {rankings.home && rankings.away && (
        <div className="mt-6">
          <p>ホーム順位: {rankings.home}</p>
          <p>アウェイ順位: {rankings.away}</p>
        </div>
      )}

      {result !== null && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold">予測結果</h2>
          <p>
            結果: {result === 0 ? "アウェイ勝利" : result === 1 ? "引き分け" : "ホーム勝利"}
          </p>
          <p>ホーム勝利: {probs.home_win}</p>
          <p>引き分け: {probs.draw}</p>
          <p>アウェイ勝利: {probs.away_win}</p>
        </div>
      )}
    </div>
  );
}

export default App;
