// src/SchedulePage.jsx
import React, { useEffect, useState } from "react";

function SchedulePage({ onMatchClick }) {
  const [matches, setMatches] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/next_matches")
      .then(res => res.json())
      .then(data => {
        setMatches(data.matches);  // ← ここ重要！そのまま配列で受け取る
      });
  }, []);

  return (
    <div className="bg-white p-4 rounded-xl shadow mb-6">
      <h2 className="text-xl font-semibold mb-4">Upcoming Matches</h2>
      <ul>
        {matches.map((match, idx) => (
          <li
            key={idx}
            className="mb-2 p-3 border rounded-lg hover:bg-blue-100 cursor-pointer"
            onClick={() => onMatchClick(match.home, match.away)}
          >
            {match.home} vs {match.away} <span className="text-sm text-gray-500">({new Date(match.date).toLocaleString()})</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default SchedulePage;
