// src/App.js
import React, { useState, useEffect } from "react";
import { Button } from "./components/ui/button";
import {
  Select,
  SelectTrigger,
  SelectValue,
  SelectContent,
  SelectItem,
} from "./components/ui/select";
import { Card, CardContent } from "./components/ui/card";
import { BarChart, Bar, XAxis, YAxis, Tooltip, LucideTableCellsSplit, ResponsiveContainer } from "recharts";

function App() {
  const [homeTeam, setHomeTeam] = useState("");
  const [awayTeam, setAwayTeam] = useState("");
  const [teamOptions, setTeamOptions] = useState([]);
  const [result, setResult] = useState(null);
  const [probs, setProbs] = useState(null);
  const [rankings, setRankings] = useState({ home: null, away: null });
  const [homeForm, setHomeForm] = useState([]);
  const [awayForm, setAwayForm] = useState([]);

  useEffect(() => {
    fetch("http://127.0.0.1:8000/teams")
      .then((res) => res.json())
      .then((data) => {
        setTeamOptions(data.teams);
      });
  }, []);

  const handlePredict = async () => {
    const featureRes = await fetch(
      `http://127.0.0.1:8000/features?home=${homeTeam}&away=${awayTeam}`
    );
    const featureData = await featureRes.json();
    setHomeForm(featureData.home_form);
    setAwayForm(featureData.away_form);
    setRankings({ home: featureData.home_rank, away: featureData.away_rank });

    const res = await fetch("http://127.0.0.1:8000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ features: featureData.features }),
    });

    const data = await res.json();
    setResult(data.result);
    setProbs(data.probabilities);
  };

  const renderForm = (formArray) =>
    formArray.map((result, index) => (
      <span key={index} className={`mx-1 font-bold ${
        result === "W" ? "text-green-500" :
        result === "D" ? "text-yellow-500" :
        "text-red-500"
      }`}>
        {result}
      </span>
    ));  

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-6 rounded-2xl shadow-xl w-full max-w-md">
        <h1 className="text-2xl font-bold mb-6 text-center text-blue-700">
          predict results of premier league matches
        </h1>

        <div className="mb-4">
          <label className="block mb-1 font-medium">Home team：</label>
          <Select value={homeTeam} onValueChange={setHomeTeam}>
            <SelectTrigger className="w-full">
              <SelectValue placeholder="select team" />
            </SelectTrigger>
            <SelectContent>
              {teamOptions.map((opt) => (
                <SelectItem key={opt.value} value={opt.label}>
                  {opt.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div className="mb-4">
          <label className="block mb-1 font-medium">Away team：</label>
          <Select value={awayTeam} onValueChange={setAwayTeam}>
            <SelectTrigger className="w-full">
              <SelectValue placeholder="select team" />
            </SelectTrigger>
            <SelectContent>
              {teamOptions.map((opt) => (
                <SelectItem key={opt.value} value={opt.label}>
                  {opt.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <Button
          onClick={handlePredict}
          disabled={!homeTeam || !awayTeam}
          className="w-full mb-4"
        >
          predict!
        </Button>

        {rankings.home && rankings.away && (
          <div className="mb-4 text-sm text-gray-700">
            <p>Home team ranking: {rankings.home}</p>
            <p>Away team ranking: {rankings.away}</p>
          </div>
        )}

        {result !== null && (
          <Card>
            <CardContent className="pt-4">
              <h2 className="text-lg font-semibold mb-2">prediction</h2>
              <p>result: {result === 0 ? "Away team win" : result === 1 ? "Draw" : "Home team win"}</p>

              <div className="my-4">
                <ResponsiveContainer width="100%" height={50}>
                  <BarChart layout="vertical" data={[
                    {
                      name: "win probability",
                      home_win: probs.home_win,
                      draw: probs.draw,
                      away_win: probs.away_win,
                    },
                  ]}>
                    <Bar dataKey="home_win" stackId="a" fill="#60a5fa" />
                    <Bar dataKey="draw" stackId="a" fill="#fbbf24" />
                    <Bar dataKey="away_win" stackId="a" fill="#f87171" />
                    <XAxis type="number" hide />
                    <YAxis type="category" dataKey="name" hide />
                    <Tooltip />
                  </BarChart>
                </ResponsiveContainer>
                <div className="flex justify-between text-sm mt-1">
                  <span>Home team win: {(probs.home_win * 100).toFixed(1)}%</span>
                  <span>Draw: {(probs.draw * 100).toFixed(1)}%</span>
                  <span>Away team win: {(probs.away_win * 100).toFixed(1)}%</span>
                </div>
              </div>
            </CardContent>
          </Card>
        )}

        <div className="mb-2 text-sm text-gray-700">
          {homeTeam && (
            <p>
              Last 5 matches of <strong>{homeTeam}</strong>: {renderForm(homeForm)}
            </p>
          )}
          {awayTeam && (
            <p>
              Last 5 matches of <strong>{awayTeam}</strong>: {renderForm(awayForm)}
            </p>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
