import React, { useEffect, useState } from "react";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";

const getShortName = (fullName) => {
  const team = teamOptions.find((opt) => opt.label === fullName);
  return team ? team.value : fullName;
};

function App() {
  const [teamOptions, setTeamOptions] = useState([]);
  const [homeTeam, setHomeTeam] = useState("");
  const [awayTeam, setAwayTeam] = useState("");
  const [result, setResult] = useState(null);
  const [homeForm, setHomeForm] = useState([]);
  const [awayForm, setAwayForm] = useState([]);
  const [fixtures, setFixtures] = useState([]);

  useEffect(() => {
    // チーム一覧取得
    fetch("http://127.0.0.1:8000/teams")
      .then((res) => res.json())
      .then((data) => setTeamOptions(data.teams));

    // 次の試合一覧を取得
    fetch("http://127.0.0.1:8000/next_matches")
      .then((res) => res.json())
      .then((data) => setFixtures(data.matches));
  }, []);

  const handlePredict = () => {
    fetch(`http://127.0.0.1:8000/features?home=${homeTeam}&away=${awayTeam}`)
      .then((res) => res.json())
      .then((data) => {
        setResult(data);
        setHomeForm(data.home_form);
        setAwayForm(data.away_form);
      });
  };

  const handleFixtureClick = (home, away) => {
    setHomeTeam(home);
    setAwayTeam(away);
    fetch(`http://127.0.0.1:8000/features?home=${home}&away=${away}`)
      .then((res) => res.json())
      .then((data) => {
        setResult(data);
        setHomeForm(data.home_form);
        setAwayForm(data.away_form);
      });
  };

  const renderForm = (form) => {
    return form.map((r, i) => {
      let color = r === "W" ? "text-green-600" : r === "L" ? "text-red-600" : "text-gray-600";
      return (
        <span key={i} className={`font-bold ${color} mr-1`}>
          {r}
        </span>
      );
    });
  };

  return (
    <div className="max-w-3xl mx-auto py-10 px-4">
      <h1 className="text-3xl font-bold mb-6">プレミアリーグ 勝敗予測</h1>

      <div className="grid grid-cols-2 gap-4 mb-6">
        <div>
          <Select value={homeTeam} onValueChange={setHomeTeam}>
            <SelectTrigger>
              <SelectValue placeholder="ホームチームを選択" />
            </SelectTrigger>
            <SelectContent>
              {teamOptions.map((opt) => (
                <SelectItem key={opt.value} value={opt.label}>
                  {opt.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>

        <div>
          <Select value={awayTeam} onValueChange={setAwayTeam}>
            <SelectTrigger>
              <SelectValue placeholder="アウェイチームを選択" />
            </SelectTrigger>
            <SelectContent>
              {teamOptions.map((opt) => (
                <SelectItem key={opt.value} value={opt.label}>
                  {opt.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        </div>
      </div>

      <Button onClick={handlePredict} disabled={!homeTeam || !awayTeam} className="mb-6">
        予測する
      </Button>

      <div className="grid grid-cols-2 gap-4 mb-10">
        {fixtures.map((fixture, idx) => (
          <Button
            key={idx}
            variant="outline"
            onClick={() => handleFixtureClick(fixture.home, fixture.away)}
          >
            {getShortName(fixture.home)} vs {getShortName(fixture.away)}<br />
            <span className="text-xs text-gray-500">
              {new Date(fixture.date).toLocaleString()}
            </span>
          </Button>
        ))}
      </div>

      {result && (
        <Card>
          <CardContent className="py-6">
            <h2 className="text-xl font-semibold mb-4">予測結果</h2>
            <p className="mb-2">
              結果: <span className="font-bold">{result.prediction}</span>
            </p>
            <Progress value={result.prob_win * 100} className="mb-1" />
            <p>ホーム勝利: {(result.prob_win * 100).toFixed(1)}%</p>
            <Progress value={result.prob_draw * 100} className="mb-1 mt-2" />
            <p>引き分け: {(result.prob_draw * 100).toFixed(1)}%</p>
            <Progress value={result.prob_lose * 100} className="mb-1 mt-2" />
            <p>アウェイ勝利: {(result.prob_lose * 100).toFixed(1)}%</p>

            <div className="mt-6">
              <p>
                {getShortName(homeTeam)} Recent 5 Matches: {renderForm(homeForm)}
              </p>
              <p>
                {getShortName(awayTeam)} Recent 5 Matches: {renderForm(awayForm)}
              </p>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

export default App;
