// src/App.js
import React, { useState, useEffect } from "react";
import { Button } from "./components/ui/button";
import { Card, CardContent } from "./components/ui/card";
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer } from "recharts";

// 🔽仮の対戦カード（次節の情報）
const fixtures = [
  { home: "Arsenal Football Club", away: "Chelsea Football Club" },
  { home: "Liverpool Football Club", away: "Manchester City Football Club" },
  { home: "Tottenham Hotspur Football Club", away: "Everton Football Club" },
];

function App() {
  const [teamOptions, setTeamOptions] = useState([]);
  const [selectedFixture, setSelectedFixture] = useState(null);
  const [result, setResult] = useState(null);
  const [probs, setProbs] = useState(null);
  const [rankings, setRankings] = useState({ home: null, away: null });
  const [homeForm, setHomeForm] = useState([]);
  const [awayForm, setAwayForm] = useState([]);
  const [fixtures, setFixtures] = useState([]);


  useEffect(() => {
    // チーム一覧取得
    fetch("http://127.0.0.1:8000/teams")
      .then((res) => res.json())
      .then((data) => setTeamOptions(data.teams));

    // 次の試合一覧を取得
    fetch("http://127.0.0.1:8000/next_matches")
      .then((res) => res.json())
      .then((data) => setFixtures(data.matches)); // ← schedule.json の "matches" 配列を使用
  }, []);

  const getShortName = (label) => {
    const team = teamOptions.find((opt) => opt.label === label);
    return team ? team.value : label;
  };

  const handleFixtureClick = async (home, away) => {
    setSelectedFixture({ home, away });
    const featureRes = await fetch(`http://127.0.0.1:8000/features?home=${home}&away=${away}`);
    const featureData = await featureRes.json();
    setHomeForm(featureData.home_form);
    setAwayForm(featureData.away_form);
    setRankings({ home: featureData.home_rank, away: featureData.away_rank });

    const res = await fetch("http://127.0.0.1:8000/predict", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ features: featureData.features }),
    });
    const data = await res.json();
    setResult(data.result);
    setProbs(data.probabilities);
  };

  const renderForm = (formArray) =>
    formArray.map((result, index) => (
      <span key={index} className={`mx-1 font-bold ${
        result === "W" ? "text-green-500" :
        result === "D" ? "text-yellow-500" :
        "text-red-500"
      }`}>
        {result}
      </span>
    ));

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      <div className="bg-white p-6 rounded-2xl shadow-xl w-full max-w-xl">
        <h1 className="text-2xl font-bold mb-4 text-center text-blue-700">Premier League Match Predictor</h1>

        <div className="mb-4">
          <p className="font-medium text-sm mb-2">Select a fixture:</p>
          <div className="grid grid-cols-1 gap-2">
            {fixtures.map((fixture, idx) => (
              <Button
                key={idx}
                onClick={() => handleFixtureClick(fixture.home, fixture.away)}
              >
                {getShortName(fixture.home)} vs {getShortName(fixture.away)}
              </Button>
            ))}
          </div>
        </div>

        {selectedFixture && (
          <>
            <div className="mb-4 text-sm text-gray-700">
              <p>🏠 {getShortName(selectedFixture.home)} Ranking: {rankings.home}</p>
              <p>🚌 {getShortName(selectedFixture.away)} Ranking: {rankings.away}</p>
            </div>

            {result !== null && (
              <Card className="mb-4">
                <CardContent className="pt-4">
                  <h2 className="text-lg font-semibold mb-2">Prediction Result</h2>
                  <p>Result: {result === 0 ? "Away Win" : result === 1 ? "Draw" : "Home Win"}</p>
                  <ResponsiveContainer width="100%" height={50}>
                    <BarChart layout="vertical" data={[{
                      name: "Prediction",
                      home_win: probs.home_win,
                      draw: probs.draw,
                      away_win: probs.away_win,
                    }]}>
                      <Bar dataKey="home_win" stackId="a" fill="#60a5fa" />
                      <Bar dataKey="draw" stackId="a" fill="#fbbf24" />
                      <Bar dataKey="away_win" stackId="a" fill="#f87171" />
                      <XAxis type="number" hide />
                      <YAxis type="category" dataKey="name" hide />
                      <Tooltip />
                    </BarChart>
                  </ResponsiveContainer>
                  <div className="flex justify-between text-sm mt-2">
                    <span>🏠 Win: {(probs.home_win * 100).toFixed(1)}%</span>
                    <span>🤝 Draw: {(probs.draw * 100).toFixed(1)}%</span>
                    <span>🚌 Win: {(probs.away_win * 100).toFixed(1)}%</span>
                  </div>
                </CardContent>
              </Card>
            )}

            <div className="text-sm text-gray-700">
              <p>Last 5 matches of <strong>{getShortName(selectedFixture.home)}</strong>: {renderForm(homeForm)}</p>
              <p>Last 5 matches of <strong>{getShortName(selectedFixture.away)}</strong>: {renderForm(awayForm)}</p>
            </div>
          </>
        )}
      </div>
    </div>
  );
}

export default App;
